app.addLabelEntry("Enter Amount")


def press(button): 
    if button == "Submit":
        global amount
        amount = float(app.getEntry("Enter Amount"))
        global currency
        currency = app.getOptionBox("Current Currency")
        global currencyto
        currencyto = app.getOptionBox("Convert to")
        api_response = client.latest(base_currency=currency, currencies=[currencyto])
        value = float(api_response["data"][f"{currencyto}"]["value"])
        result = round(value * amount, 3)
        app.addMessage("result", result)


app.addButtons(["Submit"], press)


app.go()